import React, { useState, useEffect } from 'react';
import { getTasks, createTask, updateTask, deleteTask } from '../api';

function Dashboard({ token }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchData() {
      try {
        const tasksData = await getTasks(token);
        setTasks(tasksData);
      } catch (error) {
        setError(error.message);
      }
    }
    fetchData();
  }, [token]);

  const handleCreate = async () => {
    if (!title) return setError('Título es requerido');
    try {
      const task = await createTask(token, { title, description });
      setTasks([...tasks, task]);
      setTitle('');
      setDescription('');
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdate = async (taskId, updatedFields) => {
    try {
      const updated = await updateTask(token, taskId, updatedFields);
      setTasks(tasks.map((t) => (t._id === taskId ? updated : t)));
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (taskId) => {
    try {
      await deleteTask(token, taskId);
      setTasks(tasks.filter((t) => t._id !== taskId));
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div>
      <h2>Dashboard de Tareas</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div>
        <input
          placeholder="Título"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          placeholder="Descripción"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button onClick={handleCreate}>Crear Tarea</button>
      </div>

      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            <strong>{task.title}</strong> - {task.status}
            <button onClick={() => handleUpdate(task._id, { status: 'En progreso' })}>
              En progreso
            </button>
            <button onClick={() => handleUpdate(task._id, { status: 'Completadas' })}>
              Completar
            </button>
            <button onClick={() => handleDelete(task._id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
