import React, { useEffect, useState } from 'react';
import { getTasks, createTask, updateTask, deleteTask } from '../api';

function Dashboard({ token }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTasks();
  }, [token]);

  const fetchTasks = async () => {
    try {
      const data = await getTasks(token);
      setTasks(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleCreate = async () => {
    if (!title) return setError('Título es requerido');
    try {
      const newTask = await createTask(token, { title, description });
      setTasks([...tasks, newTask]);
      setTitle('');
      setDescription('');
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdate = async (taskId, updatedFields) => {
    try {
      const updated = await updateTask(token, taskId, updatedFields);
      setTasks(tasks.map(t => (t._id === taskId ? updated : t)));
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (taskId) => {
    try {
      await deleteTask(token, taskId);
      setTasks(tasks.filter(t => t._id !== taskId));
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div>
      <h2>Dashboard de Tareas</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <input placeholder="Título" value={title} onChange={e => setTitle(e.target.value)} />
        <input placeholder="Descripción" value={description} onChange={e => setDescription(e.target.value)} />
        <button onClick={handleCreate}>Crear Tarea</button>
      </div>
      <ul>
        {tasks.map(task => (
          <li key={task._id}>
            <strong>{task.title}</strong> - {task.status}
            <button onClick={() => handleUpdate(task._id, { status: 'En progreso' })}>En progreso</button>
            <button onClick={() => handleUpdate(task._id, { status: 'Completadas' })}>Completar</button>
            <button onClick={() => handleDelete(task._id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
